import React, { useState } from 'react';
import { useForm } from 'react-hook-form';
import axios from 'axios';
import toast from 'react-hot-toast';
import { 
  Activity, 
  Brain, 
  AlertTriangle, 
  CheckCircle, 
  Clock, 
  Pill,
  User,
  Plus,
  X,
  Loader2
} from 'lucide-react';

const SymptomChecker = () => {
  const [symptoms, setSymptoms] = useState([]);
  const [currentSymptom, setCurrentSymptom] = useState('');
  const [analysis, setAnalysis] = useState(null);
  const [loading, setLoading] = useState(false);
  const [selectedLanguage, setSelectedLanguage] = useState('en');
  
  const { register, handleSubmit, reset, formState: { errors } } = useForm();

  const commonSymptoms = [
    'fever', 'headache', 'cough', 'fatigue', 'nausea', 'chest pain',
    'abdominal pain', 'dizziness', 'shortness of breath', 'back pain',
    'sore throat', 'runny nose', 'muscle aches', 'loss of appetite',
    'insomnia', 'anxiety', 'depression', 'rash', 'swelling', 'joint pain'
  ];

  const languages = {
    en: 'English',
    hi: 'हिंदी',
    te: 'తెలుగు',
    ta: 'தமிழ்',
    kn: 'ಕನ್ನಡ',
    bn: 'বাংলা'
  };

  const addSymptom = () => {
    if (currentSymptom.trim() && !symptoms.includes(currentSymptom.trim().toLowerCase())) {
      setSymptoms([...symptoms, currentSymptom.trim().toLowerCase()]);
      setCurrentSymptom('');
    }
  };

  const removeSymptom = (symptomToRemove) => {
    setSymptoms(symptoms.filter(symptom => symptom !== symptomToRemove));
  };

  const addCommonSymptom = (symptom) => {
    if (!symptoms.includes(symptom)) {
      setSymptoms([...symptoms, symptom]);
    }
  };

  const analyzeSymptoms = async () => {
    if (symptoms.length < 2) {
      toast.error('Please add at least 2 symptoms for analysis');
      return;
    }

    setLoading(true);
    try {
      const response = await axios.post('/api/symptoms/check', {
        symptoms: symptoms,
        language: selectedLanguage
      });

      setAnalysis(response.data);
      toast.success('Analysis completed successfully!');
    } catch (error) {
      toast.error(error.response?.data?.error || 'Failed to analyze symptoms');
    } finally {
      setLoading(false);
    }
  };

  const getSeverityColor = (severity) => {
    switch (severity.toLowerCase()) {
      case 'high':
        return 'badge-danger';
      case 'medium':
        return 'badge-warning';
      case 'low':
        return 'badge-success';
      default:
        return 'badge-secondary';
    }
  };

  return (
    <div className="max-w-4xl mx-auto space-y-8">
      {/* Header */}
      <div className="text-center">
        <div className="flex justify-center mb-4">
          <Activity className="h-12 w-12 text-primary-600" />
        </div>
        <h1 className="text-3xl font-bold text-gray-900 mb-2">
          AI Symptom Checker
        </h1>
        <p className="text-gray-600">
          Describe your symptoms and get instant AI-powered analysis and medical advice
        </p>
      </div>

      {/* Language Selector */}
      <div className="card">
        <div className="flex items-center justify-between mb-4">
          <h2 className="text-lg font-semibold text-gray-900">Language</h2>
          <select
            value={selectedLanguage}
            onChange={(e) => setSelectedLanguage(e.target.value)}
            className="input-field w-auto"
          >
            {Object.entries(languages).map(([code, name]) => (
              <option key={code} value={code}>{name}</option>
            ))}
          </select>
        </div>
      </div>

      {/* Symptom Input */}
      <div className="card">
        <h2 className="text-lg font-semibold text-gray-900 mb-4">
          Add Your Symptoms
        </h2>
        
        <div className="space-y-4">
          {/* Manual Input */}
          <div className="flex gap-2">
            <input
              type="text"
              value={currentSymptom}
              onChange={(e) => setCurrentSymptom(e.target.value)}
              placeholder="Enter a symptom (e.g., fever, headache)"
              className="input-field flex-1"
              onKeyPress={(e) => e.key === 'Enter' && addSymptom()}
            />
            <button
              onClick={addSymptom}
              className="btn-primary px-4"
            >
              <Plus className="h-4 w-4" />
            </button>
          </div>

          {/* Selected Symptoms */}
          {symptoms.length > 0 && (
            <div>
              <h3 className="text-sm font-medium text-gray-700 mb-2">Selected Symptoms:</h3>
              <div className="flex flex-wrap gap-2">
                {symptoms.map((symptom, index) => (
                  <span
                    key={index}
                    className="badge badge-success flex items-center space-x-1"
                  >
                    <span>{symptom}</span>
                    <button
                      onClick={() => removeSymptom(symptom)}
                      className="ml-1 hover:text-red-600"
                    >
                      <X className="h-3 w-3" />
                    </button>
                  </span>
                ))}
              </div>
            </div>
          )}

          {/* Common Symptoms */}
          <div>
            <h3 className="text-sm font-medium text-gray-700 mb-2">Common Symptoms:</h3>
            <div className="flex flex-wrap gap-2">
              {commonSymptoms.map((symptom) => (
                <button
                  key={symptom}
                  onClick={() => addCommonSymptom(symptom)}
                  disabled={symptoms.includes(symptom)}
                  className={`badge ${
                    symptoms.includes(symptom)
                      ? 'bg-gray-100 text-gray-400 cursor-not-allowed'
                      : 'bg-primary-100 text-primary-700 hover:bg-primary-200 cursor-pointer'
                  }`}
                >
                  {symptom}
                </button>
              ))}
            </div>
          </div>

          {/* Analyze Button */}
          <div className="pt-4">
            <button
              onClick={analyzeSymptoms}
              disabled={symptoms.length < 2 || loading}
              className="btn-primary w-full flex items-center justify-center space-x-2 disabled:opacity-50 disabled:cursor-not-allowed"
            >
              {loading ? (
                <>
                  <Loader2 className="h-5 w-5 animate-spin" />
                  <span>Analyzing...</span>
                </>
              ) : (
                <>
                  <Brain className="h-5 w-5" />
                  <span>Analyze Symptoms</span>
                </>
              )}
            </button>
          </div>
        </div>
      </div>

      {/* Analysis Results */}
      {analysis && (
        <div className="space-y-6">
          {/* Possible Conditions */}
          <div className="card">
            <h2 className="text-lg font-semibold text-gray-900 mb-4 flex items-center space-x-2">
              <Brain className="h-5 w-5 text-primary-600" />
              <span>Possible Conditions</span>
            </h2>
            
            <div className="space-y-3">
              {analysis.possible_conditions.map((condition, index) => (
                <div key={index} className="border border-gray-200 rounded-lg p-4">
                  <div className="flex items-center justify-between mb-2">
                    <h3 className="font-medium text-gray-900">{condition.condition}</h3>
                    <span className={`badge ${getSeverityColor(condition.severity)}`}>
                      {condition.severity}
                    </span>
                  </div>
                  <div className="flex items-center space-x-4 text-sm text-gray-600">
                    <span>Confidence: {condition.confidence}%</span>
                  </div>
                </div>
              ))}
            </div>
          </div>

          {/* Medical Advice */}
          <div className="card">
            <h2 className="text-lg font-semibold text-gray-900 mb-4 flex items-center space-x-2">
              <User className="h-5 w-5 text-primary-600" />
              <span>Medical Advice</span>
            </h2>
            
            <div className="grid md:grid-cols-2 gap-6">
              {/* Immediate Actions */}
              {analysis.medical_advice.immediate_action.length > 0 && (
                <div>
                  <h3 className="font-medium text-gray-900 mb-2 flex items-center space-x-2">
                    <AlertTriangle className="h-4 w-4 text-red-600" />
                    <span>Immediate Actions</span>
                  </h3>
                  <ul className="space-y-1">
                    {analysis.medical_advice.immediate_action.map((action, index) => (
                      <li key={index} className="text-sm text-gray-700 flex items-start space-x-2">
                        <span className="text-red-600 mt-1">•</span>
                        <span>{action}</span>
                      </li>
                    ))}
                  </ul>
                </div>
              )}

              {/* Home Remedies */}
              {analysis.medical_advice.home_remedies.length > 0 && (
                <div>
                  <h3 className="font-medium text-gray-900 mb-2 flex items-center space-x-2">
                    <CheckCircle className="h-4 w-4 text-medical-600" />
                    <span>Home Remedies</span>
                  </h3>
                  <ul className="space-y-1">
                    {analysis.medical_advice.home_remedies.map((remedy, index) => (
                      <li key={index} className="text-sm text-gray-700 flex items-start space-x-2">
                        <span className="text-medical-600 mt-1">•</span>
                        <span>{remedy}</span>
                      </li>
                    ))}
                  </ul>
                </div>
              )}

              {/* Medications */}
              {analysis.medical_advice.medications.length > 0 && (
                <div>
                  <h3 className="font-medium text-gray-900 mb-2 flex items-center space-x-2">
                    <Pill className="h-4 w-4 text-blue-600" />
                    <span>Medications</span>
                  </h3>
                  <ul className="space-y-1">
                    {analysis.medical_advice.medications.map((medication, index) => (
                      <li key={index} className="text-sm text-gray-700 flex items-start space-x-2">
                        <span className="text-blue-600 mt-1">•</span>
                        <span>{medication}</span>
                      </li>
                    ))}
                  </ul>
                </div>
              )}

              {/* When to See Doctor */}
              {analysis.medical_advice.when_to_see_doctor.length > 0 && (
                <div>
                  <h3 className="font-medium text-gray-900 mb-2 flex items-center space-x-2">
                    <Clock className="h-4 w-4 text-orange-600" />
                    <span>When to See Doctor</span>
                  </h3>
                  <ul className="space-y-1">
                    {analysis.medical_advice.when_to_see_doctor.map((advice, index) => (
                      <li key={index} className="text-sm text-gray-700 flex items-start space-x-2">
                        <span className="text-orange-600 mt-1">•</span>
                        <span>{advice}</span>
                      </li>
                    ))}
                  </ul>
                </div>
              )}
            </div>

            {/* Emergency Signs */}
            {analysis.medical_advice.emergency_signs.length > 0 && (
              <div className="mt-6 p-4 bg-red-50 border border-red-200 rounded-lg">
                <h3 className="font-medium text-red-900 mb-2 flex items-center space-x-2">
                  <AlertTriangle className="h-4 w-4 text-red-600" />
                  <span>Emergency Warning Signs</span>
                </h3>
                <ul className="space-y-1">
                  {analysis.medical_advice.emergency_signs.map((sign, index) => (
                    <li key={index} className="text-sm text-red-800 flex items-start space-x-2">
                      <span className="text-red-600 mt-1">•</span>
                      <span>{sign}</span>
                    </li>
                  ))}
                </ul>
              </div>
            )}
          </div>

          {/* Disclaimer */}
          <div className="bg-yellow-50 border border-yellow-200 rounded-lg p-4">
            <p className="text-sm text-yellow-800">
              <strong>Disclaimer:</strong> This analysis is for informational purposes only and should not replace professional medical advice. 
              Always consult with a healthcare provider for proper diagnosis and treatment.
            </p>
          </div>
        </div>
      )}
    </div>
  );
};

export default SymptomChecker;
